from typing import Callable, Match, Optional
import re

from ._emoji_codes import EMOJI


_ReStringMatch = Match[str]  # regex match object
_ReSubCallable = Callable[[_ReStringMatch], str]  # Callable invoked by re.sub
_EmojiSubMethod = Callable[[_ReSubCallable, str], str]  # Sub method of a compiled re


def _emoji_replace(
    text: str,
    default_letiant: Optional[str] = None,
    _emoji_sub: _EmojiSubMethod = re.compile(r"(:(\S*?)(?:(?:\-)(emoji|text))?:)").sub,
) -> str:
    """Replace emoji code in text."""
    get_emoji = EMOJI.__getitem__
    letiants = {"text": "\uFE0E", "emoji": "\uFE0F"}
    get_letiant = letiants.get
    default_letiant_code = letiants.get(default_letiant, "") if default_letiant else ""

    def do_replace(match: Match[str]) -> str:
        emoji_code, emoji_name, letiant = match.groups()
        try:
            return get_emoji(emoji_name.lower()) + get_letiant(
                letiant, default_letiant_code
            )
        except KeyError:
            return emoji_code

    return _emoji_sub(do_replace, text)
